SELECT Equipment.EquipmentID, Make, Model, Description FROM Equipment JOIN EquipmentLoan  ON EquipmentLoan.EquipmentID=Equipment.EquipmentID WHERE Equipment.Damaged = "0" AND `Current` = "0"AND (Equipment.Type LIKE "%video%" OR Equipment.Description LIKE "%video%");
SELECT Employee.EmployeeID, FirstName, LastName FROM Employee JOIN Contract ON Contract.EmployeeID = Employee.EmployeeID WHERE DueFinish LIKE "2019%";
SELECT Title, Amount AS "Expenses" FROM Project LEFT JOIN Expense ON Expense.ProjectID = Project.ProjectID;
SELECT DISTINCT FirstName, LastName, Email, Grade.Title AS "Grade" FROM Employee JOIN Grade ON Grade.GradeID = Employee.GradeID JOIN EmployeeSkill ON EmployeeSkill.EmployeeID = Employee.EmployeeID JOIN Skill ON Skill.SkillID = EmployeeSkill.SkillID WHERE Skill.Title LIKE "%video%";
SELECT Project.Title, COUNT(Assignment.ProjectID) AS "Number of Employees Assigned" FROM Project JOIN Assignment ON Assignment.ProjectID = Project.ProjectID JOIN Employee ON Employee.EmployeeID = Assignment.EmployeeID WHERE Employee.Current = "1" AND Project.Internal = "0" GROUP BY Assignment.ProjectID HAVING COUNT(Assignment.ProjectID) >2;
SELECT Employee.EmployeeID, Employee.FirstName, Employee.LastName FROM Employee WHERE Employee.EmployeeID NOT IN (SELECT EmployeeID FROM Assignment);
SELECT Skill.Title AS "Skill Name", COUNT(EmployeeSkill.SkillID) AS "Number of Skilled Employees" FROM Skill JOIN EmployeeSkill ON EmployeeSkill.SkillID = Skill.SkillID JOIN Employee ON Employee.EmployeeID = EmployeeSkill.EmployeeID GROUP BY EmployeeSkill.SkillID HAVING COUNT(EmployeeSkill.SkillID) >= 1;
SELECT Employee.EmployeeID, COUNT(EquipmentLoan.EquipmentID) AS "Equipment Loans" FROM Employee JOIN EquipmentLoan ON EquipmentLoan.EmployeeID = Employee.EmployeeID WHERE EquipmentLoan.Current = "1" GROUP BY EquipmentLoan.EmployeeID HAVING COUNT(EquipmentLoan.EmployeeID) >= 1;
SELECT Employee.EmployeeID, COUNT(EquipmentLoan.EquipmentID) AS "All Time Loans" FROM Employee JOIN EquipmentLoan ON EquipmentLoan.EmployeeID = Employee.EmployeeID WHERE EquipmentLoan.Current = "1" OR EquipmentLoan.Current = "0" GROUP BY EquipmentLoan.EmployeeID HAVING COUNT(EquipmentLoan.EmployeeID) >= 1;
SELECT Employee.EmployeeID, Payslip.PayslipID FROM Employee JOIN Payslip ON Employee.EmployeeID=Payslip.EmployeeID LEFT JOIN Contract ON Employee.EmployeeID=Contract.EmployeeID WHERE Employee.Current = "0" AND Payslip.Payday >= "2017-01-01" AND Payslip.Payday <= "2017-12-31";
